mapper.update:

	update post
	set 


	
		title = #{ title },
	


	
		contents = #{ contents }, 
	
 

		category_no = #{ categoryNo }
	where no = #{ no };

mapper.select:

	select p.title, date_format(p.reg_date, '%Y/%m/%d %H:%i:%s') as regDate
	from post p, category c
	where c.no = ${ value }
		and p.category_no = c.no
	order by p.reg_date desc;
